package wdemo;

public class DoWhiledemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		

		int i=2;
		do
		{
			System.out.print("Welcome to java");
			i--;
		}while(i>2);
		
		
		
		
		
		
		while(i>2) //1>1
		{
			System.out.print("Welcome to java");
			i--;
		}

	}

}

	
